// src/components/TimetableGrid.tsx
import React from 'react';
import { useTimetable } from './hooks/useTimetable';

type Props = {
  /** 已有课程的数据 */
  lessons: Lesson[];
  /** 点击已有课程时触发，进入编辑 */
  onEdit: (lesson: Lesson) => void;
  /** 点击空白单元格时触发，传入 weekday(0-4) 和 period(1-6) */
  onAddAt: (weekday: number, period: number) => void;
  /** 更新课程（主要用于 +/− 修改欠席数） */
  onUpdate: (lesson: Lesson) => void;
  /** 根据缺席率返回圆点颜色 */
  getIndicatorColor: (lesson: Lesson) => string;
};

export const TimetableGrid: React.FC<Props> = ({
  lessons,
  onEdit,
  onAddAt,
  onUpdate,
  getIndicatorColor,
}) => {
  const weekdays = ['月', '火', '水', '木', '金'];

  return (
    <div className="timetable">
      {/* 第一行：空格 + 星期标题 */}
      <div className="header-cell" />
      {weekdays.map((d) => (
        <div key={d} className="header-cell">
          {d}曜
        </div>
      ))}

      {/* 1限～6限 */}
      {[1, 2, 3, 4, 5, 6].map((p) => (
        <React.Fragment key={p}>
          {/* 左侧显示限数 */}
          <div className="period-cell">{p}限</div>

          {weekdays.map((_, w) => {
            const lesson = lessons.find(
              (ls) => ls.weekday === w && ls.period === p
            );
            return (
              <div
                key={w}
                className="lesson-cell"
                onClick={() =>
                  lesson
                    ? onEdit(lesson)         // 编辑已有课程
                    : onAddAt(w, p)         // 添加新课程
                }
              >
                {lesson && (
                  <div
                    className="lesson-block"
                    style={{ backgroundColor: lesson.color }}
                  >
                    <div>{lesson.title}</div>
                    <div>{lesson.room}</div>

                    {/* 出欠控制 +/−，并阻止冒泡 */}
                    <div>
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          onUpdate({
                            ...lesson,
                            absences: Math.max(lesson.absences - 1, 0),
                          });
                        }}
                      >
                        −
                      </button>
                      <span>{lesson.absences}</span>
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          onUpdate({
                            ...lesson,
                            absences: lesson.absences + 1,
                          });
                        }}
                      >
                        ＋
                      </button>
                    </div>

                    {/* 右上小圆点指示颜色 */}
                    <div
                      className="indicator"
                      style={{ backgroundColor: getIndicatorColor(lesson) }}
                    />
                  </div>
                )}
              </div>
            );
          })}
        </React.Fragment>
      ))}
    </div>
  );
};
