// src/components/TimetableGrid.tsx
import React from 'react';
import { Lesson } from '../hooks/useTimetable';

type Props = {
  lessons: Lesson[];
  onEdit: (lesson: Lesson) => void;
  getIndicatorColor: (lesson: Lesson) => string;
};

// 星期一～五的日语表示
const weekdays = ['月', '火', '水', '木', '金'];

export const TimetableGrid: React.FC<Props> = ({
  lessons,
  onEdit,
  getIndicatorColor,
}) => {
  return (
    <div className="timetable">
      {/* 顶部空白格 + 星期标题 */}
      <div className="header-cell"></div>
      {weekdays.map((d) => (
        <div key={d} className="header-cell">{d}曜</div>
      ))}

      {/* 1限～6限 */}
      {[1, 2, 3, 4, 5, 6].map((p) => (
        <React.Fragment key={p}>
          {/* 每行左侧显示「n限」 */}
          <div className="period-cell">{p}限</div>

          {/* 每个时段 5 天 */}
          {weekdays.map((_, w) => {
            const lesson = lessons.find(
              (ls) => ls.weekday === w && ls.period === p
            );
            return (
              <div
                key={w}
                className="lesson-cell"
                onClick={() => lesson && onEdit(lesson)}
              >
                {lesson && (
                  <div
                    className="lesson-block"
                    style={{ backgroundColor: lesson.color }}
                  >
                    <div>{lesson.title}</div>
                    <div>{lesson.room}</div>
                    <div>
      <button
        onClick={e => {
          e.stopPropagation();
          onEdit({ ...lesson, absences: lesson.absences - 1 });
        }}
      >−</button>
      <span>{lesson.absences}</span>
      <button
        onClick={e => {
          e.stopPropagation();
          onEdit({ ...lesson, absences: lesson.absences + 1 });
        }}
      >＋</button>
    </div> 　
                    <div
                      className="indicator"
                      style={{ backgroundColor: getIndicatorColor(lesson) }}
                    ></div>
                  </div>
                )}
              </div>
            );
          })}
        </React.Fragment>
      ))}
    </div>
  );
};
