// src/components/LessonFormModal.tsx
import React, { useState, useEffect } from 'react';
import { Lesson } from '../hooks/useTimetable';
import { ColorPicker } from './ColorPicker';

type Props = {
  isOpen: boolean;
  onClose: () => void;
  onAdd: (lesson: Lesson) => void;
  editLesson?: Lesson | null;
};

export const LessonFormModal: React.FC<Props> = ({
  isOpen,
  onClose,
  onAdd,
  editLesson,
}) => {
  // 各フィールドの state
  const [weekday, setWeekday] = useState<0 | 1 | 2 | 3 | 4>(0);
  const [period, setPeriod] = useState<1 | 2 | 3 | 4 | 5 | 6>(1);
  const [title, setTitle] = useState<string>('');
  const [professor, setProfessor] = useState<string>('');
  const [room, setRoom] = useState<string>('');
  const [required, setRequired] = useState<number>(0);
  const [absences, setAbsences] = useState<number>(0);
  const [color, setColor] = useState<string>('#e6194B');

  // 編集モード時に editLesson の内容を同期
  useEffect(() => {
    if (editLesson) {
      setWeekday(editLesson.weekday);
      setPeriod(editLesson.period);
      setTitle(editLesson.title);
      setProfessor(editLesson.professor);
      setRoom(editLesson.room);
      setRequired(editLesson.required);
      setAbsences(editLesson.absences);
      setColor(editLesson.color);
    }
  }, [editLesson]);

  if (!isOpen) return null;

  const handleSubmit = () => {
    const lesson: Lesson = {
      id: editLesson ? editLesson.id : Date.now().toString(),
      weekday,
      period,
      title,
      professor,
      room,
      required,
      absences,
      color,
    };
    onAdd(lesson);
    onClose();
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>科目を追加／編集</h2>

        <label>
          曜日：
          <select
            value={weekday}
            onChange={e =>
              setWeekday(Number(e.target.value) as 0 | 1 | 2 | 3 | 4)
            }
          >
            <option value={0}>月曜日</option>
            <option value={1}>火曜日</option>
            <option value={2}>水曜日</option>
            <option value={3}>木曜日</option>
            <option value={4}>金曜日</option>
          </select>
        </label>

        <label>
          限：
          <select
            value={period}
            onChange={e =>
              setPeriod(Number(e.target.value) as 1 | 2 | 3 | 4 | 5 | 6)
            }
          >
            {[1, 2, 3, 4, 5, 6].map(n => (
              <option key={n} value={n}>
                {n}限
              </option>
            ))}
          </select>
        </label>

        <label>
          科目名：
          <input
            type="text"
            value={title}
            onChange={e => setTitle(e.target.value)}
          />
        </label>

        <label>
          教授名：
          <input
            type="text"
            value={professor}
            onChange={e => setProfessor(e.target.value)}
          />
        </label>

        <label>
          教室：
          <input
            type="text"
            value={room}
            onChange={e => setRoom(e.target.value)}
          />
        </label>

        <label>
          必要出席回数：
          <input
            type="number"
            value={required}
            onChange={e => setRequired(Number(e.target.value))}
          />
        </label>

        <label>
          欠席回数：
          <input
            type="number"
            value={absences}
            onChange={e => setAbsences(Number(e.target.value))}
          />
        </label>

        <label>
          色：
          <ColorPicker selected={color} onSelect={setColor} />
        </label>

        <div className="modal-buttons">
          <button onClick={handleSubmit}>保存</button>
          <button onClick={onClose}>キャンセル</button>
        </div>
      </div>
    </div>
  );
};
