// src/App.tsx
import React, { useState, useEffect } from 'react';
import { useTimetable } from './hooks/useTimetable';
import { SemesterSelector } from './components/SemesterSelector';
import { TimetableGrid } from './components/TimetableGrid';
import { LessonFormModal } from './components/LessonFormModal';
import { Semester, Lesson } from './types';
import './App.css';

const App: React.FC = () => {
  const {
    lessons,
    addLesson,
    updateLesson,
    getIndicatorColor,
  } = useTimetable();

  const [semester, setSemester] = useState<Semester>(() => {
    const saved = localStorage.getItem('selectedSemester');
    return (saved as Semester) || '2025 前期';
  });
  useEffect(() => {
    localStorage.setItem('selectedSemester', semester);
  }, [semester]);

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editLesson, setEditLesson] = useState<Lesson | null>(null);

  // 点击“科目块”或“科目追加”时调用
  const openModalForAdd = () => {
    setEditLesson(null);
    setIsModalOpen(true);
  };
  const openModalForEdit = (lesson: Lesson) => {
    setEditLesson(lesson);
    setIsModalOpen(true);
  };

  // Modal 提交后的回调
  const handleSubmit = (lesson: Lesson) => {
    if (editLesson) updateLesson(lesson);
    else addLesson(lesson);
    setIsModalOpen(false);
  };

  return (
    <div className="App">
      <h1>時間割／出欠管理</h1>
      <SemesterSelector
        semester={semester}
        setSemester={setSemester}
      />

      <button onClick={openModalForAdd}>科目追加</button>

      <TimetableGrid
        lessons={lessons}
        onEdit={openModalForEdit}
        getIndicatorColor={getIndicatorColor}
      />

      <LessonFormModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onAdd={handleSubmit}
      />
</div>
  );
};

export default App;
