import { useState, useEffect } from 'react';

export type Lesson = {
  id: string;
  weekday: 0 | 1 | 2 | 3 | 4;
  period: 1 | 2 | 3 | 4 | 5 | 6;
  title: string;
  professor: string;
  room: string;
  required: number;
  color: string;
  absences: number;
};

export function useTimetable() {
  const [lessons, setLessons] = useState<Lesson[]>([]);

 /** 删除指定 id 的课程 */
const removeLesson = (id: string) => {
setLessons(prev => prev.filter(l => l.id !== id));
};

// 组件挂载时读
useEffect(() => {
    const raw = localStorage.getItem('timetable');
    if (raw) setLessons(JSON.parse(raw));
  }, []);

  // lessons 变更时写
  useEffect(() => {
    localStorage.setItem('timetable', JSON.stringify(lessons));
  }, [lessons]);


  const addLesson = (lesson: Lesson) => {
    setLessons(prev => [...prev, lesson]);
  };

  const updateLesson = (updated: Lesson) => {
    setLessons(prev =>
      prev.map(l => (l.id === updated.id ? updated : l))
    );
  };

  const getIndicatorColor = (lesson: Lesson) => {
    const ratio = lesson.absences / lesson.required;
    if (ratio <= 1/3) return 'green';
    if (ratio <= 2/3) return 'orange';
    if (ratio < 1)    return 'red';
    return 'black';
  };

  return { lessons, addLesson, updateLesson, removeLesson, getIndicatorColor, };
}
